import { ChangesetItemType } from '@contentful/changeset-types'
import { Link } from 'contentful-management'
import { WithSys } from './types'

export const createEntityLink = <LinkType extends ChangesetItemType>(
  item: WithSys,
  linkType: LinkType
): Link<LinkType> => {
  return { sys: { id: item.sys.id, linkType, type: 'Link' } }
}
