import type { Patch } from 'generate-json-patch';
import type { Link } from 'contentful-management';
export type ChangesetChangeType = 'delete' | 'add' | 'update';
export type ChangesetItemType = 'Entry' | 'Asset' | 'ContentType' | 'EditorInterface' | string;
export type BaseChangesetItem<T extends ChangesetChangeType> = {
    changeType: T;
};
export type ChangesetEntityLink<EntityType extends ChangesetItemType = string> = {
    entity: Link<EntityType>;
};
export type DeletedChangesetItem<EntityType extends ChangesetItemType = string> = BaseChangesetItem<'delete'> & ChangesetEntityLink<EntityType>;
export type AddedChangesetItem<EntityType extends ChangesetItemType = string> = BaseChangesetItem<'add'> & ChangesetEntityLink<EntityType> & {
    data: any;
};
export type ChangedChangesetItem<EntityType extends ChangesetItemType = string> = BaseChangesetItem<'update'> & ChangesetEntityLink<EntityType> & {
    patch: Patch;
};
export type ChangesetItem<EntityType extends ChangesetItemType = string> = DeletedChangesetItem<EntityType> | AddedChangesetItem<EntityType> | ChangedChangesetItem<EntityType>;
export type Changeset<EntityType extends ChangesetItemType = string> = {
    sys: {
        type: 'Changeset';
    };
    items: Array<ChangesetItem<EntityType>>;
};
